INSERT INTO comics (isbn, title, author, price, ishardcover, synopsis, photo, stock) VALUES ("7596060791480","Thunderbolts Vol. 2 Nº 09: Tumores Maligno","Fabian Nieza",3.90,false,"Sigue un número más la saga a doble banda entre héroes y villanos, que aún se prolongará durante un par de episodios más hasta llegar al cierre de la colección USA.¿Qué ocurrirá después?","ThunderboltsVol2N09.jpg",1);
